     1→/**
     2→# Feature Module: KDE Plasma Desktop Environment
     3→
     4→## Description
     5→Complete KDE Plasma 6 desktop environment with comprehensive Wayland support,
     6→integrated theming via Stylix, and a curated suite of KDE applications optimized
     7→for productivity and development workflows.
     8→
     9→## Platform Support
    10→- ✅ NixOS (Full desktop environment)
    11→- ❌ Darwin (KDE Plasma is Linux-only)
    12→
    13→## What This Enables
    14→### Core Desktop
    15→- **KDE Plasma 6**: Wayland-first desktop with SDDM
    16→- **Window Management**: KWin compositor with effects and tiling
    17→- **Activities & Workspaces**: Multi-desktop organization
    18→- **Panels & Widgets**: Customizable desktop layout
    19→
    20→### Application Suite
    21→- **Core Tools**: Dolphin, Kate, Konsole, Spectacle
    22→- **Productivity**: Okular, Ark, KCalc, KRunner
    23→- **Media**: Elisa, Gwenview, Dragon Player
    24→- **Development**: KDevelop, Kompare, Umbrello
    25→- **Communication**: Kontact, KMail, KDE Connect
    26→
    27→### System Integration
    28→- **Theming**: Automatic Stylix integration via Qt
    29→- **Hardware**: Audio (PipeWire), Bluetooth, Network
    30→- **Services**: Printing, Location, Power management
    31→- **Security**: Polkit, KWallet, PAM integration
    32→
    33→## Stylix Integration
    34→KDE theming works automatically when `stylix.targets.qt.enable = true`
    35→is set in your rice configuration. All Qt/KDE applications inherit
    36→the Base16 color scheme through Qt theming.
    37→*/
    38→{
    39→  delib,
    40→  pkgs,
    41→  inputs,
    42→  lib,
    43→  ...
    44→}: let
    45→  inherit (delib) singleEnableOption;
    46→in
    47→  delib.module {
    48→    name = "features.kde";
    49→    options = singleEnableOption false;
    50→
    51→    nixos.ifEnabled = {
    52→      environment.systemPackages = let
    53→        # Core KDE Plasma packages
    54→        coreDesktop = with pkgs.kdePackages; [
    55→          plasma-desktop
    56→          plasma-workspace
    57→          plasma-workspace-wallpapers
    58→          kwin
    59→          systemsettings
    60→          krunner
    61→          kglobalaccel
    62→        ];
    63→
    64→        # Essential applications
    65→        coreApps = with pkgs.kdePackages; [
    66→          dolphin
    67→          dolphin-plugins
    68→          konsole
    69→          kate
    70→          spectacle
    71→          okular
    72→          gwenview
    73→          ark
    74→          kcalc
    75→        ];
    76→
    77→        # File system and I/O
    78→        fileSystem = with pkgs.kdePackages; [
    79→          kio
    80→          kio-extras
    81→          kio-fuse
    82→          baloo
    83→          baloo-widgets
    84→        ];
    85→
    86→        # Media and multimedia
    87→        mediaApps = with pkgs.kdePackages; [
    88→          elisa
    89→          dragon
    90→          k3b
    91→        ];
    92→
    93→        # Communication and PIM
    94→        communicationApps = with pkgs.kdePackages; [
    95→          kontact
    96→          kmail
    97→          kaddressbook
    98→          korganizer
    99→          kdeconnect-kde
   100→        ];
   101→
   102→        # Development tools
   103→        devTools = with pkgs.kdePackages; [
   104→          kdevelop
   105→          kompare
   106→        ];
   107→
   108→        # System integration
   109→        systemIntegration = with pkgs.kdePackages; [
   110→          discover
   111→          powerdevil
   112→          plasma-browser-integration
   113→          plasma-thunderbolt
   114→          plasma-nm
   115→          bluedevil
   116→          print-manager
   117→        ];
   118→
   119→        # Themes and appearance
   120→        themes = with pkgs.kdePackages; [
   121→          breeze
   122→          breeze-icons
   123→          breeze-gtk
   124→          oxygen
   125→          oxygen-icons
   126→        ];
   127→
   128→        # Security and wallet
   129→        security = with pkgs.kdePackages; [
   130→          kwallet
   131→          kwallet-pam
   132→          kwalletmanager
   133→          ksshaskpass
   134→        ];
   135→
   136→        # Utilities
   137→        utilities = with pkgs.kdePackages; [
   138→          filelight
   139→          kcharselect
   140→          kcolorchooser
   141→          kfind
   142→          ktimer
   143→          kruler
   144→        ];
   145→
   146→        # Optional games
   147→        games = with pkgs.kdePackages; [
   148→          kmahjongg
   149→          kpat
   150→        ];
   151→
   152→        # Third-party applications with good KDE integration
   153→        thirdParty = with pkgs; [
   154→          firefox
   155→          vlc
   156→          libreoffice-qt
   157→          gimp
   158→          inkscape
   159→          audacity
   160→          obs-studio
   161→          qt6ct
   162→        ];
   163→      in
   164→        coreDesktop
   165→        ++ coreApps
   166→        ++ fileSystem
   167→        ++ mediaApps
   168→        ++ communicationApps
   169→        ++ devTools
   170→        ++ systemIntegration
   171→        ++ themes ++ security ++ utilities ++ games ++ thirdParty;
   172→
   173→      environment.variables = {
   174→        # Session and desktop identification
   175→        XDG_SESSION_TYPE = "wayland";
   176→        XDG_SESSION_DESKTOP = lib.mkDefault "KDE";
   177→        XDG_CURRENT_DESKTOP = lib.mkDefault "KDE";
   178→        KDE_SESSION_VERSION = "6";
   179→        KDE_FULL_SESSION = "true";
   180→
   181→        # Qt/Wayland configuration
   182→        QT_QPA_PLATFORM = lib.mkDefault "wayland;xcb";
   183→        QT_WAYLAND_DISABLE_WINDOWDECORATION = "1";
   184→        QT_AUTO_SCREEN_SCALE_FACTOR = "1";
   185→        QT_SCALE_FACTOR_ROUNDING_POLICY = "RoundPreferFloor";
   186→
   187→        # Theme integration (overridden by Stylix when active)
   188→        GTK_THEME = lib.mkDefault "Breeze";
   189→
   190→        # Input method configuration
   191→        GTK_IM_MODULE = "fcitx";
   192→        QT_IM_MODULE = "fcitx";
   193→        XMODIFIERS = "@im=fcitx";
   194→      };
   195→
   196→      # Plasma locale configuration
   197→      environment.etc."xdg/plasma-localerc".text = ''
   198→        [Formats]
   199→        LANG=en_US.UTF-8
   200→      '';
   201→
   202→      # Essential programs
   203→      programs = {
   204→        dconf.enable = true;
   205→        kdeconnect.enable = true;
   206→        partition-manager.enable = true;
   207→      };
   208→
   209→      # System services configuration
   210→      services = {
   211→        # Desktop environment
   212→        desktopManager.plasma6.enable = true;
   213→        displayManager = {
   214→          sddm = {
   215→            enable = true;
   216→            wayland.enable = true;
   217→            theme = "breeze";
   218→            autoNumlock = true;
   219→          };
   220→          gdm.enable = lib.mkForce false;
   221→        };
   222→
   223→        # Core system services
   224→        dbus.enable = true;
   225→        udisks2.enable = true;
   226→        upower.enable = true;
   227→        accounts-daemon.enable = true;
   228→
   229→        # Audio stack
   230→        pipewire = {
   231→          enable = true;
   232→          pulse.enable = true;
   233→          alsa.enable = true;
   234→          jack.enable = true;
   235→        };
   236→
   237→        # Connectivity
   238→        blueman.enable = true;
   239→
   240→        # Printing and discovery
   241→        printing.enable = true;
   242→        avahi = {
   243→          enable = true;
   244→          nssmdns4 = true;
   245→          openFirewall = true;
   246→        };
   247→
   248→        # Hardware and power
   249→        fwupd.enable = true;
   250→        thermald.enable = true;
   251→        power-profiles-daemon.enable = false;
   252→
   253→        # File system services
   254→        gvfs.enable = true;
   255→        tumbler.enable = true;
   256→
   257→        # Additional services
   258→        geoclue2.enable = true;
   259→      };
   260→
   261→      # Input method configuration
   262→      i18n.inputMethod = {
   263→        enable = true;
   264→        type = "fcitx5";
   265→        fcitx5 = {
   266→          waylandFrontend = true;
   267→          plasma6Support = true;
   268→          addons = with pkgs; [
   269→            fcitx5-gtk
   270→          ];
   271→        };
   272→      };
   273→
   274→      # XDG configuration
   275→      xdg = {
   276→        portal = {
   277→          enable = true;
   278→          extraPortals = [
   279→            pkgs.kdePackages.xdg-desktop-portal-kde
   280→            pkgs.xdg-desktop-portal-gtk
   281→          ];
   282→          config = {
   283→            common.default = ["kde"];
   284→            kde = {
   285→              default = ["kde" "gtk"];
   286→              "org.freedesktop.impl.portal.FileChooser" = ["kde"];
   287→              "org.freedesktop.impl.portal.AppChooser" = ["kde"];
   288→              "org.freedesktop.impl.portal.Print" = ["kde"];
   289→              "org.freedesktop.impl.portal.Notification" = ["kde"];
   290→            };
   291→          };
   292→        };
   293→
   294→        # Default applications
   295→        mime = {
   296→          enable = true;
   297→          defaultApplications = {
   298→            # Web
   299→            "text/html" = lib.mkDefault "firefox.desktop";
   300→            "x-scheme-handler/http" = lib.mkDefault "firefox.desktop";
   301→            "x-scheme-handler/https" = lib.mkDefault "firefox.desktop";
   302→            # Files
   303→            "inode/directory" = lib.mkDefault "org.kde.dolphin.desktop";
   304→            "text/plain" = lib.mkDefault "org.kde.kate.desktop";
   305→            # Images
   306→            "image/jpeg" = lib.mkDefault "org.kde.gwenview.desktop";
   307→            "image/png" = lib.mkDefault "org.kde.gwenview.desktop";
   308→            # Documents
   309→            "application/pdf" = lib.mkDefault "org.kde.okular.desktop";
   310→            # Audio/Video
   311→            "audio/mpeg" = lib.mkDefault "org.kde.elisa.desktop";
   312→            "video/mp4" = lib.mkDefault "vlc.desktop";
   313→            # Archives
   314→            "application/zip" = lib.mkDefault "org.kde.ark.desktop";
   315→          };
   316→        };
   317→      };
   318→
   319→      # Security configuration
   320→      security = {
   321→        polkit.enable = true;
   322→        rtkit.enable = true;
   323→        pam.services = {
   324→          sddm.enableGnomeKeyring = true;
   325→          sddm-autologin.enableGnomeKeyring = true;
   326→        };
   327→      };
   328→
   329→      # Hardware configuration
   330→      hardware = {
   331→        enableAllFirmware = true;
   332→        bluetooth = {
   333→          enable = true;
   334→          powerOnBoot = true;
   335→          settings.General = {
   336→            Enable = "Source,Sink,Media,Socket";
   337→            Experimental = "true";
   338→          };
   339→        };
   340→      };
   341→
   342→      # System services
   343→      systemd.user.services.plasma-localed = {
   344→        description = "Plasma localed proxy";
   345→        wantedBy = ["graphical-session.target"];
   346→        serviceConfig = {
   347→          ExecStart = "${pkgs.kdePackages.plasma-workspace}/libexec/plasma-localed";
   348→          Restart = "on-failure";
   349→        };
   350→      };
   351→
   352→      # Font configuration
   353→      fonts = {
   354→        enableDefaultPackages = true;
   355→        packages = with pkgs; [
   356→          # Primary fonts
   357→          noto-fonts
   358→          noto-fonts-cjk-sans
   359→          noto-fonts-emoji
   360→          fira-code
   361→          fira-code-symbols
   362→          # Development fonts
   363→          source-code-pro
   364→          source-sans-pro
   365→          source-serif-pro
   366→          # System fonts
   367→          liberation_ttf
   368→          ubuntu_font_family
   369→          cantarell-fonts
   370→          # Specialized fonts
   371→          mplus-outline-fonts.githubRelease
   372→          dina-font
   373→          proggyfonts
   374→        ];
   375→
   376→        fontconfig = {
   377→          enable = true;
   378→          defaultFonts = {
   379→            monospace = ["Fira Code" "Source Code Pro"];
   380→            sansSerif = ["Noto Sans" "Source Sans Pro"];
   381→            serif = ["Noto Serif" "Source Serif Pro"];
   382→            emoji = ["Noto Color Emoji"];
   383→          };
   384→        };
   385→      };
   386→    };
   387→
   388→    home.ifEnabled = {
   389→      imports = [inputs.plasma-manager.homeManagerModules.plasma-manager];
   390→
   391→      # Simplified plasma configuration to resolve CI issues with unsupported options
   392→      programs.plasma = {
   393→        enable = true;
   394→      };
   395→
   396→      # KDE application configurations
   397→      programs = {
   398→        # Text editor with LSP support
   399→        kate = {
   400→          enable = true;
   401→        };
   402→
   403→        # Terminal emulator
   404→        konsole = {
   405→          enable = true;
   406→        };
   407→
   408→        # Document viewer
   409→        okular = {
   410→          enable = true;
   411→        };
   412→
   413→        # Music player
   414→        elisa = {
   415→          enable = true;
   416→        };
   417→
   418→        # Markdown editor
   419→        ghostwriter = {
   420→          enable = true;
   421→        };
   422→      };
   423→
   424→      # Additional KDE packages for Home Manager
   425→      home.packages = with pkgs.kdePackages; [
   426→        # Extra utilities
   427→        filelight
   428→        kcharselect
   429→        kcolorchooser
